mapper.update:
update tbl_notice  
set hit = hit + 1
where no = #{no}

update tbl_notice
set title = #{title},
content =
#{content}
where no = #{no}
set TITLE = #{TITLE},
CONTENT = #{CONTENT}
where NO = #{NO}

mapper.select:

select 
	no,title,content,reg_date,hit
from (
	select /*+INDEX_DESC(tbl_notice pk_tbl_notice) */
	rownum rn, no,title,content,reg_date,hit from tbl_notice
where rownum <= #{pageNum} * #{amount}
)
where rn>(#{pageNum}-1) * #{amount}
