mapper.select:
select * from tbl_product
	where high_code = #{high_code}
	and low_code = #{lowCode}

SELECT no, product_code, product_color, product_size, product_stock FROM tbl_product_detail WHERE product_code = #{product_code}
AND product_color = #{product_color}
AND product_color is null

SELECT
	no
FROM
	tbl_product_detail
WHERE
	product_code = #{product_code}

		AND product_color = #{product_color}


		AND product_color is null


		AND product_size = #{product_size}


		AND product_size is null

SELECT rownum, r.no, m.id, m.lev, r.title, r.content, r.reg_date, r.hit, r.file_path, r.file_name, r.recommend, r.grade, pd.no as product_detail_no, pd.product_code, pd.product_color, pd.product_size FROM tbl_member m, (SELECT rownum, no, member_id, title, content, reg_date, hit, file_path, file_name, product_detail_no, recommend, grade FROM tbl_review ORDER BY
no desc
recommend desc
grade desc
grade asc
) r, tbl_product_detail pd WHERE r.product_detail_no = pd.no AND m.id = r.member_id AND pd.product_code = #{product_code} AND rownum <= #{limit}

select * from (
	select A.*, rownum as rn from tbl_product A order by code) 
where rn between #{start} and #{finish}
