mapper.select:

SELECT
    no, member_id, product_code, title, content, reg_date, hit, ref, depth, step, pno, answer, secret
FROM
    (SELECT
        /*+ INDEX_DESC(tbl_qna pk_tbl_qna) */
        rownum rn, no, member_id, product_code, title, content, reg_date, hit, ref, depth, step, pno, answer, secret
    FROM
        tbl_qna 
    WHERE



        rownum <= #{pageNum} * #{amount}
    )
WHERE
    rn > (#{pageNum} - 1) * #{amount}
