mapper.update:
update tbl_review
set hit = hit + 1
where no = #{no}

update tbl_review
set title = #{title},
content =
#{content}
where no = #{no}

mapper.select:

select 
	no, member_id, title, content, reg_date, hit
from (

	select /*+INDEX_DESC(tbl_review pk_tbl_review) */
	rownum rn, no,title,content,reg_date,hit 

	from tbl_review
where 




	rownum <=#{reviewPageNum} * #{reviewAmount}
	)
where rn > (#{reviewPageNum}-1) * #{reviewAmount}


select 
	no,title,content,reg_date,hit
from (
	select /*+INDEX_DESC(tbl_review pk_tbl_review) */
	rownum rn, no,title,content,reg_date,hit 
	from tbl_review
where member_id = #{member_id}  and 




	rownum <=#{reviewPageNum} * #{reviewAmount}
	)
where rn > (#{reviewPageNum}-1) * #{reviewAmount}
