mapper.update:
UPDATE
	tbl_cart
SET
	quantity = #{quantity}
WHERE
	no = #{no}

UPDATE
    tbl_member
SET
    point = point - #{used_point}
WHERE
    id = #{member_id}

UPDATE
    tbl_product
SET
    sale_count = sale_count + #{quantity}
WHERE
    code = #{product_code}

UPDATE
    tbl_product_detail
SET
    product_stock = product_stock - #{quantity}
WHERE
    no = #{product_detail_no}

mapper.delete:
DELETE FROM
	tbl_cart 
WHERE 
	product_detail_no = #{product_detail_no} 
	AND member_id = #{member_id}

mapper.update:
UPDATE
	tbl_order_detail
SET 
	state = #{state}
WHERE
	order_code = #{order_code}

UPDATE
	tbl_product
SET
	sale_count = sale_count - #{quantity}
WHERE
	code = #{product_code}

UPDATE
	tbl_product_detail
SET
	product_stock = product_stock + #{quantity}
WHERE
	no = #{product_detail_no}

UPDATE
    tbl_member
SET
    point = point + #{used_point}
WHERE
    id = #{member_id}

UPDATE
    tbl_order_detail 
SET
    state = #{state}
WHERE
    order_code = ${order_code}
    AND product_detail_no = #{product_detail_no}

UPDATE
	tbl_product
SET
	grade = #{grade}
WHERE
	code = #{product_code}
